sourcesContent:
import user from '../models/User';
import role from '../models/Role';
import { combineResolvers } from 'graphql-resolvers'
import access from '../middlewares/accessMiddlewares'

const facultyType = {
    Query: {
        getAllFaculty: combineResolvers(access.accessAdmin, async () => {
            try {
                const faculty = await role.findOne({ roleName: "faculty" })
                const getAllFaculty = await user.find({ isDeleted: false, role: faculty._id });

                if (getAllFaculty.length > 0) {
                    return getAllFaculty
                }
                else {
                    throw new Error("Data is Not Found");
                }
            } catch (error) {
                throw new Error(error.message);
            }
        }),
        getFaculty: combineResolvers(access.accessAdmin, async (parent, args, context, info) => {
            try {
                const getFaculty = await user.find({ _id: args.id, isDeleted: false });

                if (getFaculty.length > 0) {
                    return getFaculty;
                }
                else {
                    throw new Error("Data is Not Found");
                }
            } catch (error) {
                throw new Error(error.message);
            }
        })
    },
    Mutation: {
        createFaculty: combineResolvers(access.accessAdmin, async (parent, args, context, info) => {
            try {
                const email = args.facultymodel.email;
                const checkEmail = await user.findOne({ email });
                const faculty = await role.findOne({ roleName: "faculty" });

                if (!checkEmail) {
                    const createFaculty = await user.create(args.facultymodel);
                    createFaculty.role = faculty._id;
                    createFaculty.save();
                    return createFaculty;
                }
                else {
                    throw new Error("Email already exists");
                }
            } catch (error) {
                throw new Error(error.message);
            }
        }),
        updateFaculty: combineResolvers(access.accessAdmin, async (parent, args, context, info) => {
            try {
                const email = args.facultymodel.email
                const checkEmail = await user.findOne({ email })
                const getFaculty = await user.find({ _id: args.id, isDeleted: false });
                if (getFaculty.length > 0) {
                    if (!checkEmail || checkEmail._id.toString() === args.id) {
                        const updateFaculty = await user.findByIdAndUpdate(args.id, args.facultymodel, { new: true });
                        return updateFaculty;
                    }
                    else {
                        throw new Error("Email already exists");
                    }
                }
                else {
                    throw new Error("Data is Not Found");
                }

            } catch (error) {
                throw new Error(error.message);
            }
        }),
        deleteFaculty: combineResolvers(access.accessAdmin, async (parent, args, context, info) => {
            try {
                const deleteFaculty = await user.findByIdAndUpdate(args.id, { $set: { isDeleted: true } }, { new: true });
                return deleteFaculty;
            } catch (error) {
                throw new Error(error.message);
            }
        })
    }
}
export default facultyType;

