              args?.id,
              { $set: { isDeleted: true } },
              { new: true }
            );
            return deleteStudent;
          } else {
            throw new Error("Student is Not Found!");
          }
        } catch (error) {
          throw new Error(error?.message);
        }
      }
    ),
  },
